SELECT NVL((select distinct(a.cod_Cabe_bore) from int_Rec_linea_borec a where A.CARE_OID_CABE_RECL = OID_CABE_RECL and rownum = 1),0) numero_br, TEMP.*, (SELECT CB.COD_CABE_BORE FROM rec_tipo_movim mov, rec_opera_recla ror, rec_linea_opera_recla lor, int_rec_linea_borec lb, int_rec_cabec_borec cb WHERE mov.oid_tipo_movi = lor.timo_oid_tipo_movi AND ror.care_oid_cabe_recl = oid_cabe_recl AND lor.opre_oid_oper_recl = ror.oid_oper_recl AND mov.cod_tipo_movi = 'D' and lor.OID_LINE_OPER_RECL = lb.LOR_OID_LINE_OPER_RECL(+) and lb.COD_CABE_BORE = cb.COD_CABE_BORE(+) and cb.COD_CABE_BORE is not null and rownum = 1) NROBR, (SELECT INT_PKG_RECLA.gen_fn_devue_deest_borec( CB.ESBO_OID_ESTA_BOR1) FROM rec_tipo_movim mov, rec_opera_recla ror, rec_linea_opera_recla lor, int_rec_linea_borec lb, int_rec_cabec_borec cb WHERE mov.oid_tipo_movi = lor.timo_oid_tipo_movi AND ror.care_oid_cabe_recl = oid_cabe_recl AND lor.opre_oid_oper_recl = ror.oid_oper_recl AND mov.cod_tipo_movi = 'D' and lor.OID_LINE_OPER_RECL = lb.LOR_OID_LINE_OPER_RECL(+) and lb.COD_CABE_BORE = cb.COD_CABE_BORE(+) and cb.COD_CABE_BORE is not null and rownum = 1) ESTADOBR , (SELECT INT_PKG_RECLA.gen_fn_devue_deest_borec( CB.ESBO_OID_ESTA_BOR2) FROM rec_tipo_movim mov, rec_opera_recla ror, rec_linea_opera_recla lor, int_rec_linea_borec lb, int_rec_cabec_borec cb WHERE mov.oid_tipo_movi = lor.timo_oid_tipo_movi AND ror.care_oid_cabe_recl = oid_cabe_recl AND lor.opre_oid_oper_recl = ror.oid_oper_recl AND mov.cod_tipo_movi = 'D' and lor.OID_LINE_OPER_RECL = lb.LOR_OID_LINE_OPER_RECL(+) and lb.COD_CABE_BORE = cb.COD_CABE_BORE(+) and cb.COD_CABE_BORE is not null and rownum = 1) RESULTADOBR FROM (
SELECT * FROM (
SELECT cr.OID_CABE_RECL, cr.NUM_RECL numero_reclamo, sc.VAL_NUME_SOLI numero_solicitud, TO_CHAR(cr.FEC_INGR,'dd/MM/yyyy') FEC_INGR, pe.VAL_NOMB_PERI peri_regi, pe2.VAL_NOMB_PERI peri_refe, INT_PKG_RECLA.REC_FN_DEVUE_ESTAD_CDR(cr.OID_CABE_RECL, 'RCR') estado_reclamo, cr.NUM_TOTA_DEVU total_devuelve, cr.NUM_TOTA_ENVI total_envia, HIP_PKG_CONSU.HIP_FN_DEVUE_IMPOR_ATEND(cr.OID_CABE_RECL) real_atendido, cr.IMP_SALD_PAGA imp_reclamado, (HIP_PKG_CONSU.HIP_FN_DEVUE_IMPOR_ATEND(cr.OID_CABE_RECL) - cr.NUM_TOTA_DEVU) IMP_REAL_RECLAMADO, HIP_PKG_CONSU.HIP_FN_DEVUE_UNIDA_ATEND(cr.OID_CABE_RECL) UDD_REAL_ATENDIDO, (SELECT SUM (lor.Num_Unid_recl) FROM rec_tipo_movim mov, rec_opera_recla ror, rec_linea_opera_recla lor WHERE mov.oid_tipo_movi = lor.timo_oid_tipo_movi AND ror.care_oid_cabe_recl = cr.oid_cabe_recl AND lor.opre_oid_oper_recl = ror.oid_oper_recl AND mov.cod_tipo_movi = 'D') UDD_DEVUELVE, (SELECT SUM (lor.Num_Unid_Recl) FROM rec_tipo_movim mov, rec_opera_recla ror, rec_linea_opera_recla lor WHERE mov.oid_tipo_movi = lor.timo_oid_tipo_movi AND ror.care_oid_cabe_recl = cr.oid_cabe_recl AND lor.opre_oid_oper_recl = ror.oid_oper_recl AND mov.cod_tipo_movi = 'E') UDD_ENVIA_RECLAMADA FROM REC_CABEC_RECLA cr, CRA_PERIO pe, CRA_PERIO pe2, PED_SOLIC_CABEC sc, REC_ESTAD_RECLA er WHERE cr.SOCA_OID_SOLI_CABE = sc.OID_SOLI_CABE AND cr.ESRE_OID_ESTA_RECL = er.OID_ESTA_RECL AND cr.CLIE_OID_CLIE = #oidCliente# AND pe.OID_PERI = cr.PERD_OID_PERI_RECL AND pe2.OID_PERI = cr.PERD_OID_PERI_DOCU_REFE ORDER BY cr.FEC_INGR DESC
) WHERE ROWNUM < #filas#
) TEMP

SELECT * FROM (
SELECT OID_SOLI_CABE, TO_CHAR(FEC_FACT,'dd/MM/yyyy') FEC_FACT_AUX, COD_PERI CAMPANHA, G1.VAL_I18N TIPO_SOLICITUD, HIP_PKG_CONSU.HIP_FN_OBTIE_ORIGE_PEDID_CONSO(OC.OID_OC, #codigoCliente#) ORIGEN, VAL_NUME_SOLI NRO_BOLETA, VAL_TOTA_PAGA_LOCA MONTO, VAL_PREC_CATA_TOTA_LOCA MONTO_CATALOGO, VAL_IMPO_DESC_1_TOTA_LOCA MONTO_DESCUENTO, VAL_TOTA_GAST_ADMI GASTO_ADMINISTRATIVO, VAL_TOTA_FLET_LOCA FLETE1, CCC_PKG_GENER.CCC_FN_OBTIE_MONTO_FLEXI_PEDID(OID_SOLI_CABE) FLEXIPAGO, NUM_UNID_ATEN_TOTA UNIDADES, DECODE(OC.SOCA_OID_SOLI_CABE,null,1,0) as PED_SERVICIO, COD_TERR TERRITORIO, COD_ZONA ZONA, CHEQUEO.IND_CHEQUEO IND_CHEQUEO, (SELECT IO.VAL_I18N FROM REC_RESUL_CHEQU RC, GEN_I18N_SICC_PAIS IO WHERE RC.OID_RESU_CHEQ = IO.VAL_OID AND IO.ATTR_ENTI = 'REC_RESUL_CHEQU' AND RC.OID_RESU_CHEQ = PED_SOLIC_CABEC.RECQ_OID_RESU_CHEQ) STA_CHEQUEO, PED_SOLIC_CABEC.VAL_IMPO_FLET_TOTA_LOCA FLETE, (SELECT c.sec_nume_docu FROM INT_SOLIC_CONSO_CABEC c WHERE c.soca_oid_soli_cabe_refe = oid_soli_cabe) sec_nume_docu, (SELECT max(num_docu_cont_inte) FROM fac_docum_conta_cabec c WHERE c.soca_oid_soli_cabe = oid_soli_cabe and tido_oid_tipo_docu=1) NUMERO_DE_FACTURA FROM PED_SOLIC_CABEC, MAE_CLIEN, PED_TIPO_SOLIC_PAIS, PED_TIPO_SOLIC, GEN_I18N_SICC_COMUN G1, CRA_PERIO, SEG_PERIO_CORPO, ZON_TERRI, ZON_ZONA, HIP_TIPO_SOLIC, (SELECT SOCA_OID_SOLI_CABE, OID_SOLI_CABE OID_OC FROM PED_SOLIC_CABEC A, PED_TIPO_SOLIC C, PED_TIPO_SOLIC_PAIS D, MAE_CLIEN B WHERE B.COD_CLIE = #codigoCliente# AND A.CLIE_OID_CLIE=B.OID_CLIE AND A.IND_TS_NO_CONSO=1 AND A.IND_OC = 1 AND A.FEC_FACT IS NOT NULL AND A.TSPA_OID_TIPO_SOLI_PAIS_CONS = D.OID_TIPO_SOLI_PAIS AND D.TSOL_OID_TIPO_SOLI = C.OID_TIPO_SOLI AND C.IND_SOLI_NEGA = 0) OC, (SELECT A1.OID_SOLI_CABE CAB, A1.RECQ_OID_RESU_CHEQ IND_CHEQUEO FROM PED_SOLIC_CABEC A1 WHERE A1.IND_TS_NO_CONSO = 0 AND A1.RECQ_OID_RESU_CHEQ = 2009 ) CHEQUEO WHERE COD_CLIE = #codigoCliente# AND PED_SOLIC_CABEC.CLIE_OID_CLIE=OID_CLIE AND TSPA_OID_TIPO_SOLI_PAIS=OID_TIPO_SOLI_PAIS AND PED_TIPO_SOLIC_PAIS.TSOL_OID_TIPO_SOLI = PED_TIPO_SOLIC.OID_TIPO_SOLI AND G1.ATTR_ENTI='PED_TIPO_SOLIC' AND G1.VAL_OID=OID_TIPO_SOLI AND CRA_PERIO.OID_PERI=PED_SOLIC_CABEC.PERD_OID_PERI AND CRA_PERIO.PERI_OID_PERI=SEG_PERIO_CORPO.OID_PERI AND TERR_OID_TERR=OID_TERR AND ZZON_OID_ZONA=OID_ZONA AND IND_TS_NO_CONSO=0 AND FEC_FACT IS NOT NULL AND OC.SOCA_OID_SOLI_CABE (+) = OID_SOLI_CABE AND PED_TIPO_SOLIC.COD_TIPO_SOLI = HIP_TIPO_SOLIC.COD_TIPO_SOLI AND CHEQUEO.CAB (+) = OID_SOLI_CABE ORDER BY FEC_FACT DESC, COD_PERI
) WHERE ROWNUM < #filas#
