COMU FROM MAE_CLIEN_COMUN, MAE_TIPO_COMUN WHERE MAE_CLIEN_COMUN.TICM_OID_TIPO_COMU = MAE_TIPO_COMUN.OID_TIPO_COMU AND MAE_TIPO_COMUN.COD_TIPO_COMU = 'ML' AND MAE_CLIEN_COMUN.CLIE_OID_CLIE = MAE_CLIEN.OID_CLIE ) VAL_CORR_ELEC, MAE_TIPO_BLOQU.COD_TIPO_BLOQ FROM SEG_PAIS, MAE_CLIEN, MAE_CLIEN_DATOS_ADICI, MAE_CLIEN_TIPO_SUBTI, MAE_TIPO_CLIEN, MAE_ESTAD_CIVIL, MAE_ESTAT_CLIEN, SEG_TIPO_VIA, MAE_CLIEN_IDENT, MAE_CLIEN_DIREC, MAE_TIPO_DOCUM, ZON_TERRI, MAE_CLIEN_BLOQU, MAE_TIPO_BLOQU WHERE ( (SEG_PAIS.OID_PAIS = MAE_CLIEN.PAIS_OID_PAIS) AND (MAE_CLIEN.OID_CLIE = MAE_CLIEN_DATOS_ADICI.CLIE_OID_CLIE) AND (MAE_CLIEN.OID_CLIE = MAE_CLIEN_TIPO_SUBTI.CLIE_OID_CLIE) AND (MAE_TIPO_CLIEN.OID_TIPO_CLIE = MAE_CLIEN_TIPO_SUBTI.TICL_OID_TIPO_CLIE) AND (MAE_ESTAD_CIVIL.OID_ESTA_CIVI(+) = MAE_CLIEN_DATOS_ADICI.ESCV_OID_ESTA_CIVI) AND (MAE_ESTAT_CLIEN.OID_ESTA_CLIE = MAE_CLIEN_DATOS_ADICI.ESTA_OID_ESTA_CLIE) AND (MAE_CLIEN.OID_CLIE = MAE_CLIEN_IDENT.CLIE_OID_CLIE(+)) AND (MAE_CLIEN.OID_CLIE = MAE_CLIEN_DIREC.CLIE_OID_CLIE(+)) AND (SEG_TIPO_VIA.OID_TIPO_VIA(+) = MAE_CLIEN_DIREC.TIVI_OID_TIPO_VIA) AND (MAE_TIPO_DOCUM.OID_TIPO_DOCU(+) = MAE_CLIEN_IDENT.TDOC_OID_TIPO_DOCU) AND (MAE_CLIEN_DIREC.TERR_OID_TERR = ZON_TERRI.OID_TERR(+)) AND (MAE_CLIEN.OID_CLIE = MAE_CLIEN_BLOQU.CLIE_OID_CLIE(+)) AND (MAE_TIPO_BLOQU.OID_TIPO_BLOQ(+) = MAE_CLIEN_BLOQU.TIBQ_OID_TIPO_BLOQ) AND (MAE_CLIEN_DIREC.IND_DIRE_PPAL(+) = 1) AND (MAE_CLIEN_IDENT.VAL_IDEN_DOCU_PRIN(+) = 1) AND MAE_CLIEN_DIREC.IND_DIRE_PPAL = 1 AND MAE_CLIEN_DIREC.IND_ELIM = 0 AND (SEG_PAIS.COD_PAIS = #codigoPais#)
(MAE_TIPO_CLIEN.COD_TIPO_CLIE = #codigoTipoCliente#)
