SELECT DISTINCT C.COD_PLAN AS CODPLA, TRIM(TO_CHAR(COM_PKG_REPOR.COM_FN_DEVUE_COMIS_LIDER_SECCI(A.PERI_COD_PERI, A.COD_LIDE_SECC, #codigoComisionIngreso#, #codigoComisionRecu1#) , '99999990.99')) AS MONPAG FROM COM_COMIS_PERIO_CALCU A, MAE_CLIEN_LIDER C WHERE A.PERI_COD_PERI = #codigoPeriodo# AND C.PAIS_COD_PAIS = #codigoPais# AND C.COD_PLAN IS NOT NULL AND C.COD_CLID = A.COD_LIDE_SECC AND COM_PKG_REPOR.COM_FN_DEVUE_COMIS_LIDER_SECCI(A.PERI_COD_PERI, A.COD_LIDE_SECC, #codigoComisionIngreso#, #codigoComisionRecu1#) > 0 AND NOT EXISTS (SELECT X.COD_CLIB FROM MAE_CLIEN_LIDER_BLOQU X WHERE ( X.PAIS_COD_PAIS = C.PAIS_COD_PAIS AND X.COD_CLIB = A.COD_LIDE_SECC AND (#codigoPeriodo# >= X.PER_INIC) AND (#codigoPeriodo# <= X.PER_FINA OR X.PER_FINA IS NULL) AND X.EST_CLIB = '1'
) )
