and CRA_PERIO.FEC_INIC <= inscritas.FEC_INGR and CRA_PERIO.FEC_FINA >= inscritas.FEC_INGR
and rownum = 1 ) CODIGO_PERIODO, ' ' as FECHA_ENTREGA_CARNET,
case when (inscritas.FEC_ULTI_ACTU >= MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU) and (inscritas.FEC_ULTI_ACTU >= NVL(cliTelefono.FEC_ULTI_ACTU,inscritas.FEC_ULTI_ACTU)) and (inscritas.FEC_ULTI_ACTU >= NVL(cliEmail.FEC_ULTI_ACTU,inscritas.FEC_ULTI_ACTU)) and (inscritas.FEC_ULTI_ACTU >= NVL(cliCelular.FEC_ULTI_ACTU,inscritas.FEC_ULTI_ACTU)) then TO_CHAR(inscritas.FEC_ULTI_ACTU, 'YYYYMMDD') when (MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU >= inscritas.FEC_ULTI_ACTU) and (MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU >= NVL(cliTelefono.FEC_ULTI_ACTU,MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU)) and (MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU >= NVL(cliEmail.FEC_ULTI_ACTU,MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU)) and (MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU >= NVL(cliCelular.FEC_ULTI_ACTU,MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU)) then TO_CHAR(MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU, 'YYYYMMDD') when (NVL(cliTelefono.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU) >= inscritas.FEC_ULTI_ACTU) and (NVL(cliTelefono.FEC_ULTI_ACTU, MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU) >= MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU) and (NVL(cliTelefono.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU) >= NVL(cliEmail.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU)) and (NVL(cliTelefono.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU) >= NVL(cliCelular.FEC_ULTI_ACTU,inscritas.FEC_ULTI_ACTU)) then TO_CHAR(NVL(cliTelefono.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU), 'YYYYMMDD') when (NVL(cliEmail.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU) >= inscritas.FEC_ULTI_ACTU) and (NVL(cliEmail.FEC_ULTI_ACTU, MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU) >= MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU) and (NVL(cliEmail.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU) >= NVL(cliTelefono.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU)) and (NVL(cliEmail.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU) >= NVL(cliCelular.FEC_ULTI_ACTU,inscritas.FEC_ULTI_ACTU)) then TO_CHAR(NVL(cliEmail.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU), 'YYYYMMDD') when (NVL(cliCelular.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU) >= inscritas.FEC_ULTI_ACTU) and (NVL(cliCelular.FEC_ULTI_ACTU, MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU) >= MAE_CLIEN_DATOS_ADICI.FEC_ULTI_ACTU) and (NVL(cliCelular.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU) >= NVL(cliTelefono.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU)) and (NVL(cliCelular.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU) >= NVL(cliEmail.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU)) then TO_CHAR(NVL(cliCelular.FEC_ULTI_ACTU, inscritas.FEC_ULTI_ACTU), 'YYYYMMDD') end as FECHA_ULTIMA_MODIFICACION,
TO_CHAR(inscritas.FEC_INGR, 'YYYYMMDD') as FECHA_INGRESO, ' ' as ULTIMO_USUARIO_ACTUALIZO, 'C' as ORIGEN_INSCRIPCION, ' ' as CLAVE_ACCESO_DUPLAWEB
from MAE_CLIEN inscritas, MAE_CLIEN_DATOS_ADICI, MAE_CLIEN_VINCU, MAE_TIPO_VINCU, (
SELECT MAE_CLIEN.OID_CLIE, MAE_CLIEN.COD_CLIE FROM MAE_CLIEN, MAE_CLIEN_TIPO_SUBTI, MAE_CLIEN_CLASI, MAE_TIPO_CLIEN, MAE_SUBTI_CLIEN, MAE_TIPO_CLASI_CLIEN, MAE_CLASI WHERE MAE_CLIEN.OID_CLIE = MAE_CLIEN_TIPO_SUBTI.CLIE_OID_CLIE AND MAE_CLIEN_TIPO_SUBTI.OID_CLIE_TIPO_SUBT = MAE_CLIEN_CLASI.CTSU_OID_CLIE_TIPO_SUBT AND MAE_CLIEN_TIPO_SUBTI.TICL_OID_TIPO_CLIE = MAE_TIPO_CLIEN.OID_TIPO_CLIE AND MAE_CLIEN_TIPO_SUBTI.SBTI_OID_SUBT_CLIE = MAE_SUBTI_CLIEN.OID_SUBT_CLIE AND MAE_CLIEN_CLASI.TCCL_OID_TIPO_CLASI = MAE_TIPO_CLASI_CLIEN.OID_TIPO_CLAS AND MAE_CLIEN_CLASI.CLAS_OID_CLAS = MAE_CLASI.OID_CLAS AND MAE_TIPO_CLIEN.OID_TIPO_CLIE = MAE_SUBTI_CLIEN.TICL_OID_TIPO_CLIE AND MAE_SUBTI_CLIEN.OID_SUBT_CLIE = MAE_TIPO_CLASI_CLIEN.SBTI_OID_SUBT_CLIE AND MAE_TIPO_CLASI_CLIEN.OID_TIPO_CLAS = MAE_CLASI.TCCL_OID_TIPO_CLAS AND MAE_TIPO_CLIEN.COD_TIPO_CLIE = #codigoTipoCliente# AND MAE_SUBTI_CLIEN.COD_SUBT_CLIE = #codigoSubTipoCliente# AND MAE_TIPO_CLASI_CLIEN.COD_TIPO_CLAS = #codigoTipoClasificacion# AND MAE_CLASI.COD_CLAS = #codigoClasificacion#
) clientes, ( select MAE_CLIEN_COMUN.CLIE_OID_CLIE, MAE_CLIEN_COMUN.VAL_TEXT_COMU telefono, MAE_CLIEN_COMUN.FEC_ULTI_ACTU from MAE_CLIEN_COMUN, MAE_TIPO_COMUN where MAE_CLIEN_COMUN.TICM_OID_TIPO_COMU = MAE_TIPO_COMUN.OID_TIPO_COMU and MAE_TIPO_COMUN.COD_TIPO_COMU in ('TF') order by 1 ) cliTelefono, ( select MAE_CLIEN_COMUN.CLIE_OID_CLIE, MAE_CLIEN_COMUN.VAL_TEXT_COMU email, MAE_CLIEN_COMUN.FEC_ULTI_ACTU from MAE_CLIEN_COMUN, MAE_TIPO_COMUN where MAE_CLIEN_COMUN.TICM_OID_TIPO_COMU = MAE_TIPO_COMUN.OID_TIPO_COMU and MAE_TIPO_COMUN.COD_TIPO_COMU in ('ML') order by 1 ) cliEmail, ( select MAE_CLIEN_COMUN.CLIE_OID_CLIE, MAE_CLIEN_COMUN.VAL_TEXT_COMU celular, MAE_CLIEN_COMUN.FEC_ULTI_ACTU, MAE_CLIEN_COMUN.IND_COMU_PPAL from MAE_CLIEN_COMUN, MAE_TIPO_COMUN where MAE_CLIEN_COMUN.TICM_OID_TIPO_COMU = MAE_TIPO_COMUN.OID_TIPO_COMU and MAE_TIPO_COMUN.COD_TIPO_COMU in ('TM') order by 1 ) cliCelular, ( SELECT COD_PAIS, CRA_PERIO.PAIS_OID_PAIS as Pais, CRA_PERIO.MARC_OID_MARC as Marca, CRA_PERIO.CANA_OID_CANA as Canal, SEG_PERIO_CORPO.COD_PERI, SEG_PERIO_CORPO.VAL_ANIO as Anio
FROM CRA_PERIO, SEG_PERIO_CORPO, SEG_PAIS, SEG_MARCA, SEG_CANAL WHERE CRA_PERIO.PERI_OID_PERI = SEG_PERIO_CORPO.OID_PERI AND CRA_PERIO.PAIS_OID_PAIS = SEG_PAIS.OID_PAIS AND CRA_PERIO.MARC_OID_MARC = SEG_MARCA.OID_MARC AND CRA_PERIO.CANA_OID_CANA = SEG_CANAL.OID_CANA AND SEG_PERIO_CORPO.COD_PERI = #codigoPeriodo# AND SEG_PAIS.COD_PAIS = #codigoPais# AND SEG_MARCA.COD_MARC = #codigoMarca# AND SEG_CANAL.COD_CANA = #codigoCanal#
) perActual where inscritas.OID_CLIE = MAE_CLIEN_DATOS_ADICI.CLIE_OID_CLIE and inscritas.OID_CLIE = MAE_CLIEN_VINCU.CLIE_OID_CLIE_VNDO and MAE_CLIEN_VINCU.TIVC_OID_TIPO_VINC = MAE_TIPO_VINCU.OID_TIPO_VINC AND MAE_TIPO_VINCU.PAIS_OID_PAIS = perActual.Pais and inscritas.OID_CLIE = cliTelefono.CLIE_OID_CLIE(+) and inscritas.OID_CLIE = cliEmail.CLIE_OID_CLIE(+) and inscritas.OID_CLIE = cliCelular.CLIE_OID_CLIE(+) AND MAE_TIPO_VINCU.COD_TIPO_VINC = #codigoTipoVinculo# and MAE_CLIEN_VINCU.CLIE_OID_CLIE_VNTE = clientes.OID_CLIE

SELECT #codigoPais# AS CODIGO_PAIS, MAE_CLIEN.COD_CLIE AS CODIGO_CLIENTE, REC_CABEC_RECLA.NUM_RECL AS NUMERO_RECLAMO, COD_VENTA.VAL_CODI_VENT AS CODIGO_VENTA, (case WHEN REC_TIPO_MOVIM.COD_TIPO_MOVI = 'E' THEN REC_LINEA_OPERA_RECLA.IMP_CARG WHEN REC_TIPO_MOVIM.COD_TIPO_MOVI = 'D' THEN REC_LINEA_OPERA_RECLA.IMP_ABON ELSE 0 end) AS MONTO, DECODE(REC_OPERA.COD_OPER,'C','C','TM','Q','DN','Q','DE','D','FM','F','FA','F','TP','Q') AS CODIGO_OPERACION, (CASE WHEN (REC_CABEC_RECLA.ESRE_OID_ESTA_RECL = 5) THEN 'R' WHEN (REC_TIPO_MOVIM.COD_TIPO_MOVI = 'E') THEN 'E' WHEN (REC_TIPO_MOVIM.COD_TIPO_MOVI = 'D') THEN 'B' END) AS ESTADO FROM REC_CABEC_RECLA, MAE_CLIEN, MAE_CLIEN_DATOS_ADICI, REC_OPERA_RECLA, REC_LINEA_OPERA_RECLA, REC_TIPO_MOVIM, SEG_PERIO_CORPO, CRA_PERIO, REC_OPERA, REC_TIPOS_OPERA, (SELECT Z2.PROD_OID_PROD, Z2.VAL_CODI_VENT,Z1.OID_MATR_FACT, Z2.TOFE_OID_TIPO_OFER FROM PRE_MATRI_FACTU Z1, PRE_OFERT_DETAL Z2, PRE_MATRI_FACTU_CABEC Z3 WHERE z3.OID_CABE = Z1.MFCA_OID_CABE AND Z1.OFDE_OID_DETA_OFER = Z2.OID_DETA_OFER ) COD_VENTA WHERE (REC_CABEC_RECLA.CLIE_OID_CLIE = MAE_CLIEN.OID_CLIE) AND (MAE_CLIEN_DATOS_ADICI.CLIE_OID_CLIE = MAE_CLIEN.OID_CLIE) AND (REC_CABEC_RECLA.OID_CABE_RECL = REC_OPERA_RECLA.CARE_OID_CABE_RECL) AND (REC_TIPOS_OPERA.OID_TIPO_OPER = REC_OPERA_RECLA.TIOP_OID_TIPO_OPER) AND (REC_TIPOS_OPERA.ROPE_OID_OPER = REC_OPERA.OID_OPER) AND (REC_LINEA_OPERA_RECLA.OPRE_OID_OPER_RECL = REC_OPERA_RECLA.OID_OPER_RECL) AND (REC_CABEC_RECLA.PERD_OID_PERI_RECL = CRA_PERIO.OID_PERI) AND (SEG_PERIO_CORPO.OID_PERI = CRA_PERIO.PERI_OID_PERI) AND (REC_CABEC_RECLA.COD_USUA_INGR != 'CALYPSO') AND (REC_CABEC_RECLA.COD_USUA_INGR != 'BELCENTER') AND (REC_OPERA.COD_OPER IN ('C','TM','TP','DN','DE','FM','FA')) AND (REC_TIPO_MOVIM.OID_TIPO_MOVI = REC_LINEA_OPERA_RECLA.TIMO_OID_TIPO_MOVI) AND (MAE_CLIEN_DATOS_ADICI.ESTA_OID_ESTA_CLIE != 7) AND REC_LINEA_OPERA_RECLA.PROD_OID_PROD = COD_VENTA.PROD_OID_PROD (+) AND COD_VENTA.TOFE_OID_TIPO_OFER (+) = REC_LINEA_OPERA_RECLA.TOFE_OID_TIPO_OFER AND COD_VENTA.OID_MATR_FACT(+) = REC_LINEA_OPERA_RECLA.MAFA_OID_MATR_FACT AND ( (SEG_PERIO_CORPO.COD_PERI = #codigoPeriodo#)
(SEG_PERIO_CORPO.COD_PERI = #codigoNuevoPeriodo#)
)
