sqlMap.select:
select oid_peri as codigo, val_nomb_peri as descripcion from cra_perio where (fec_inic>=to_date(#fechaInicial#,'dd/mm/yyyy')
AND fec_fina<=to_date(#fechaFinal#,'dd/mm/yyyy')
) or( fec_inic<=to_date(#fechaInicial#,'dd/mm/yyyy')
and fec_fina>=to_date(#fechaFinal#,'dd/mm/yyyy')) or(fec_inic>=to_date(#fechaInicial#,'dd/mm/yyyy')
AND fec_inic<=to_date(#fechaFinal#,'dd/mm/yyyy')
) or(fec_fina>=to_date(#fechaInicial#,'dd/mm/yyyy')
AND fec_fina<=to_date(#fechaFinal#,'dd/mm/yyyy')
)

sqlMap.insert:
insert into REC_REPOR_DEUDA_PERIO_CONSO ( des_regi, des_zona, cod_terr, cod_clie, nom_clie, fec_fact, num_soli_cons, imp_cuot, imp_cuot_pend, imp_inaf_perc, imp_pend_inaf_perc, imp_afec_perc, imp_pend_afec_perc ) SELECT r.des_regi, zon.des_zona, te.cod_terr, cli.cod_clie, TRIM(cli.val_ape1)||' '||TRIM(cli.val_ape2)||' '|| TRIM(cli.val_nom1)||' '|| TRIM(cli.val_nom2) as nom_clie, per.fec_fact, per.NUM_SOLI_CONS, per.imp_cuot, per.imp_cuot_pend, per.imp_inaf_perc, per.imp_pend_inaf_perc, per.imp_afec_perc, per.imp_pend_afec_perc FROM per_cuent_corri_docle per, mae_clien_unida_admin uni_adm, mae_clien cli, zon_terri_admin ter_adm, zon_terri te, zon_secci secc, zon_zona zon, zon_regio r, (SELECT * FROM cra_perio where oid_peri= #oidPeriodoInicio# ) peri_i, (SELECT * FROM cra_perio where oid_peri= #oidPeriodoFin# ) peri_f WHERE ( (cli.oid_clie = uni_adm.clie_oid_clie) AND (cli.cod_clie = per.cod_clie) AND (ter_adm.oid_terr_admi = uni_adm.ztad_oid_terr_admi) AND (te.oid_terr = ter_adm.terr_oid_terr) AND (secc.oid_secc = ter_adm.zscc_oid_secc) AND (zon.oid_zona = secc.zzon_oid_zona) AND (r.oid_regi = zon.zorg_oid_regi) $condicion$ AND per.fec_fact = Decode(#fechaFacturacion#, null, per.fec_fact, '', per.fec_fact,To_Date(#fechaFacturacion#,'dd/MM/yyyy') )
AND (peri_i.fec_inic <= per.fec_fact) AND (peri_f.fec_fina >= per.fec_fact)
)

insert into REC_REPOR_DEUDA_PERIO_DETAL( cod_regi, des_regi, cod_zona, des_zona, cod_terr, cod_clie, nom_clie, fec_fact, fec_venc_cuot, num_soli_cons, imp_cuot, imp_cuot_pend, imp_inaf_perc, imp_pend_inaf_perc, imp_afec_perc, imp_pend_afec_perc ) SELECT r.cod_regi, r.des_regi, zon.cod_zona, zon.des_zona, te.cod_terr, cli.cod_clie, TRIM(cli.val_ape1)||' '||TRIM(cli.val_ape2)||' '|| TRIM(cli.val_nom1)||' '|| TRIM(cli.val_nom2) as nom_clie, per.fec_fact, per.fec_venc_cuot, per.NUM_SOLI_CONS, per.imp_cuot, per.imp_cuot_pend, per.imp_inaf_perc, per.imp_pend_inaf_perc, per.imp_afec_perc, per.imp_pend_afec_perc FROM per_cuent_corri_docle per, mae_clien_unida_admin uni_adm, mae_clien cli, zon_terri_admin ter_adm, zon_terri te, zon_secci secc, zon_zona zon, zon_regio r, (SELECT * FROM cra_perio where oid_peri= #oidPeriodoInicio# ) peri_i, (SELECT * FROM cra_perio where oid_peri= #oidPeriodoFin# ) peri_f WHERE ( (cli.oid_clie = uni_adm.clie_oid_clie) AND (cli.cod_clie = per.cod_clie) AND (ter_adm.oid_terr_admi = uni_adm.ztad_oid_terr_admi) AND (te.oid_terr = ter_adm.terr_oid_terr) AND (secc.oid_secc = ter_adm.zscc_oid_secc) AND (zon.oid_zona = secc.zzon_oid_zona) AND (r.oid_regi = zon.zorg_oid_regi) $condicion$ AND per.fec_fact = Decode(#fechaFacturacion#, null, per.fec_fact, '', per.fec_fact,To_Date(#fechaFacturacion#,'dd/MM/yyyy') )
AND (peri_i.fec_inic <= per.fec_fact) AND (peri_f.fec_fina >= per.fec_fact)
)
