sqlMap.select:
SELECT GEN_PKG_GENER.GEN_FN_CLIEN_DATOS_CODIG(I.COD_CLIE,'NOM_CLIE') NOMBRE, I.COD_PAIS AS COD_PAIS, I.COD_PERI AS COD_PERI, I.COD_CLIE AS COD_CLIE, I.NUM_LOTE AS NUM_LOTE, I.DOCU_COD_TIPO_DOCU AS DOCU_COD_TIPO_DOCU, I.NUM_DOCU AS NUM_DOCU, I.COD_CIA AS COD_CIA, I.TIP_REFE AS TIP_REFE, I.COD_VENT_DEVU AS COD_VENT_DEVU, ( SELECT gen_pkg_gener.GEN_FN_DEVUELVE_DESCRIPCION(MP.OID_PROD,'MAE_PRODU',#codigoIso#) FROM PRE_MATRI_FACTU_CABEC PF, PRE_OFERT PO, PRE_OFERT_DETAL P, MAE_PRODU MP WHERE PF.PERD_OID_PERI = ( SELECT max(OID_PERI_REFE) FROM INT_SOLIC_CONSO_POVEN_CABEC WHERE SEC_NUME_DOCU IN ( SELECT SEC_NUME_DOCU_CABE FROM STO_DOCUM_DIGIT WHERE SEC_NUME_DOCU=#numDocumento# AND COD_TIPO_DOCU = 'SPVD' ) ) AND PO.MFCA_OID_CABE = PF.OID_CABE AND PO.OID_OFER = P.OFER_OID_OFER AND P.VAL_CODI_VENT = TO_NUMBER(I.COD_VENT_DEVU) AND MP.OID_PROD = P.PROD_OID_PROD ) AS DESC_VENTA_DEVUELTA, I.COD_VENT_DESE as COD_VENT_DESE, (SELECT gen_pkg_gener.GEN_FN_DEVUELVE_DESCRIPCION(MP.OID_PROD,'MAE_PRODU',#codigoIso#) FROM PRE_MATRI_FACTU_CABEC PF, PRE_OFERT PO, PRE_OFERT_DETAL P, MAE_PRODU MP WHERE PF.PERD_OID_PERI = ( SELECT MAX(OID_PERI_REFE) FROM INT_SOLIC_CONSO_POVEN_CABEC WHERE SEC_NUME_DOCU IN ( SELECT SEC_NUME_DOCU_CABE FROM STO_DOCUM_DIGIT WHERE SEC_NUME_DOCU=#numDocumento# AND COD_TIPO_DOCU = 'SPVD' ) ) AND PO.MFCA_OID_CABE = PF.OID_CABE AND PO.OID_OFER = P.OFER_OID_OFER AND P.VAL_CODI_VENT = TO_NUMBER(I.COD_VENT_DESE) AND MP.OID_PROD = P.PROD_OID_PROD ) AS DESC_VENTA_DESEADA, I.CAN_VENT_DEVU AS CAN_VENT_DEVU, I.CAN_PROD_DESE AS CAN_VENT_DESE, I.STA_PROC AS STA_PROC, I.COD_MOTI_RECH AS COD_MOTI_RECH, I.COD_OPER AS COD_OPER, I.COD_TIPO_OPER AS COD_TIPO_OPER, I.SEC_NUME_DOCU as SEC_NUME_DOCU, I.COD_REGI as COD_REGI, I.COD_ZONA as COD_ZONA, I.DES_OBSE AS DES_OBSE,
I.MOT_SPV AS COD_MOT_DEVOLUCION, gen_pkg_gener.GEN_FN_DEVUELVE_DESCRIPCION((select oid_moti_devo from rec_motiv_devol where cod_moti_devo = I.MOT_SPV), 'REC_MOTIV_DEVOL', #codigoIso#) AS DESC_MOT_DEVOLUCION, ( select decode( ( select count(*) from ped_solic_posic pos, pre_ofert_detal det, pre_matri_factu mat, pre_matri_reemp ree where pos.OFDE_OID_DETA_OFER = det.OID_DETA_OFER and det.OID_DETA_OFER = mat.OFDE_OID_DETA_OFER and mat.OID_MATR_FACT = ree.MAFA_OID_COD_REEM and pos.OID_SOLI_POSI = i.OID_SOLI_POSI_DEVU ), 0, ( SELECT GEN.VAL_I18N FROM gen_i18n_sicc_comun GEN, SEG_IDIOM IDI, PED_SUBTI_POSIC BASE, ped_solic_posic pos2 WHERE GEN.IDIO_OID_IDIO = IDI.OID_IDIO AND BASE.OID_SUBT_POSI = GEN.VAL_OID AND IDI.COD_ISO_IDIO = #codigoIso# AND BASE.OID_SUBT_POSI = pos2.STPO_OID_SUBT_POSI AND GEN.ATTR_ENTI = 'PED_SUBTI_POSIC' AND pos2.OID_SOLI_POSI = I.OID_SOLI_POSI_DEVU ) , 'Reemplazo' ) FROM DUAL ) DESCRIPCION, NVL(I.VAL_PREC_CATA_ENVI,0) VAL_PREC_CATA_ENVI, NVL(I.VAL_PREC_CATA_DEVU,0) VAL_PREC_CATA_DEVU, ( select VAL_TOTA_PAGA_LOCA from ped_solic_cabec where oid_soli_cabe = c.oid_cabe
) VAL_TOTA_PAGA_LOCA, C.NUM_DOCU_CRUC NUM_DOCU_CRUC, (SELECT O.COD_PERI FROM CRA_PERIO G, SEG_PERIO_CORPO O WHERE O.OID_PERI = G.PERI_OID_PERI and G.OID_peri = C.OID_PERI_REFE) OID_PERI_REFE, I.OID_SOLI_POSI_DEVU, ( SELECT nvl(OFDE.PRECIO_UNITARIO,0) FROM PED_SOLIC_POSIC POSIC, PRE_OFERT_DETAL OFDE WHERE OID_SOLI_POSI = i.oid_soli_posi_devu AND POSIC.OFDE_OID_DETA_OFER = OFDE.OID_DETA_OFER ) PRE_UNIT_DEVU, GEN_PKG_GENER.gen_fn_devue_orige_pedid_posic((select nvl(POSIC.SOCA_OID_SOLI_CABE,0) FROM PED_SOLIC_POSIC POSIC WHERE OID_SOLI_POSI = i.oid_soli_posi_devu ),I.COD_VENT_DEVU) origen, TO_CHAR(I.FEC_DIGI, 'DD/MM/YYYY HH24:MI:SS') FEC_DIGI, I.USU_DIGI, TO_CHAR(I.FEC_MODI, 'DD/MM/YYYY HH24:MI:SS') FEC_MODI, I.USU_MODI FROM INT_SOLIC_CONSO_POVEN_DETAL I, INT_SOLIC_CONSO_POVEN_CABEC C WHERE I.COD_PAIS=#codigoPais# AND I.NUM_LOTE =#numLote# AND I.SEC_NUME_DOCU=#numDocumento# AND C.cod_pais=i.cod_pais AND C.num_docu=i.num_docu AND C.num_lote=i.num_lote and c.cod_clie =i.cod_clie

