UPDATE sto_docum_digit SET ind_envi = '0', ind_rech = '0' WHERE sec_nume_docu_cabe IN (SELECT sec_nume_docu FROM int_solic_conso_cabec
soca_oid_soli_cabe_refe =#listaOid[]#
)
